import { Producto } from "./producto";
export class DetalleFactura {
  constructor(id = 0, producto = new Producto(), cantidad = undefined, descripcion = '', porcentajeDescuento = null, valorDescuento = 0.00, porcentajeIva = null, valorIva = 0.00, precioUnitario = null, precioTotal = 0.00, esInstalacion = false) {
